mapper.update:
  UPDATE   jw_system_auth  	/* 运营系统权限表; InnoDB free: 9216 kB */


	    		 		/* 权限编码 */ 
    		 			auth_id = #{authId,jdbcType=VARCHAR},


	    		 		/* 权限名称 */ 
    		 			auth_name = #{authName,jdbcType=VARCHAR},


	    		 		/* 是否记录日志 0不记录 1记录 */ 
    		 			is_logs = #{isLogs,jdbcType=CHAR},


	    		 		/* 权限类型 0:菜单;1:功能 */ 
    		 			auth_type = #{authType,jdbcType=VARCHAR},


	    		 		/* 权限说明 */ 
    		 			auth_desc = #{authDesc,jdbcType=VARCHAR},


	    		 		/* 权限控制 */ 
    		 			auth_contr = #{authContr,jdbcType=VARCHAR},


	    		 		/* 上一级权限编码 */ 
    		 			parent_auth_id = #{parentAuthId,jdbcType=CHAR},


	    		 		/* 排序 */ 
    		 			sort_no = #{sortNo,jdbcType=INTEGER},


	    		 		/* 层级 */ 
    		 			biz_level = #{bizLevel,jdbcType=CHAR},


	    		 		/* 是否叶子节点 */ 
    		 			leaf_ind = #{leafInd,jdbcType=CHAR},


	    		 		/* 删除标志 */ 
    		 			del_stat = #{delStat,jdbcType=CHAR},


			WHERE
  	 		 		id = #{id}		/* 序号 */

mapper.select:
SELECT jsa.auth_id AS authId /* 权限编码 */ ,jsa.auth_name AS authName /* 权限名称 */ ,jsa.auth_contr AS authContr /* 权限控制 */ ,jsa.auth_desc AS authDesc /* 权限说明 */ ,jsa.parent_auth_id AS parentAuthId /* 上一级菜单编码 */ ,jsa.auth_type AS authType /* 菜单类型 */ ,jsurr.user_id AS userId /* 用户编码 */ FROM jw_system_auth AS jsa /* 运营系统权限表 */ ,jw_system_user_role_rel AS jsurr /* 运营用户与角色 */ ,jw_system_role AS jsr /* 运营角色表 */ ,jw_system_role_auth_rel AS jsrar /* 运营角色与权限表 */ WHERE 1=1 AND (jsa.del_stat = '0' OR jsa.del_stat IS NULL) AND (jsr.del_stat = '0' OR jsr.del_stat IS NULL) AND jsa.auth_type = '0' AND jsa.auth_id = jsrar.auth_id AND jsrar.role_id = jsr.role_id AND jsr.role_id = jsurr.role_id AND jsurr.user_id = #{userId}
AND jsa.parent_auth_id = #{parentAuthId}
AND (jsa.parent_auth_id is null or jsa.parent_auth_id = '')
ORDER BY jsa.sort_no ASC
